import React from "react";
import DoctorReport from "../components/DoctorReport";
import html2pdf from "html2pdf.js";

const Reports = () => {
  const reportData = {
    name: "Bhavya Kansal",
    age: 18,
    gender: "Male",
    heartRate: 76,
    spo2: 97,
    temperature: 36.5,
    ecg: 1.2,
    bloodPressure: "120/80",
    aiRisk: "Low",
    aiSuggestion: "Continue healthy lifestyle",
  };

  const handleDownloadPDF = () => {
    const element = document.getElementById("report-section");
    const opt = {
      margin: 0,
      filename: `Heart_Report_${reportData.name.replace(/\s/g, "_")}.pdf`,
      image: { type: "jpeg", quality: 0.98 },
      html2canvas: { scale: 2 },
      jsPDF: { unit: "in", format: "letter", orientation: "portrait" },
    };
    html2pdf().set(opt).from(element).save();
  };

  return (
    <div className="min-h-screen bg-[#0a072b] py-10">
      <DoctorReport data={reportData} />
      <div className="text-center mt-6">
        <button
          onClick={handleDownloadPDF}
          className="bg-green-500 hover:bg-green-600 text-white px-6 py-2 rounded-xl text-lg font-semibold transition"
        >
          Download Report as PDF
        </button>
      </div>
    </div>
  );
};

export default Reports;
