// src/components/ReportPage.jsx
import React, { useRef } from "react";
import { generatePDF } from "../utils/generatePDF";

const ReportPage = ({ name, age, riskLevel }) => {
  const reportRef = useRef();

  const currentDate = new Date().toLocaleString();

  return (
    <div className="p-8 flex flex-col items-center justify-center min-h-screen bg-gradient-to-br from-[#0f172a] to-[#1e293b] text-white">
      <div
        ref={reportRef}
        className="bg-[#1e293b] border border-[#334155] rounded-2xl shadow-xl p-10 w-full max-w-3xl"
      >
        <h1 className="text-3xl font-bold text-cyan-400 mb-6 text-center">
          ØPA AI-Powered Heart Disease Report
        </h1>

        <div className="space-y-3 text-lg">
          <p><span className="font-semibold text-cyan-300">Patient Name:</span> {name || "N/A"}</p>
          <p><span className="font-semibold text-cyan-300">Age:</span> {age || "N/A"}</p>
          <p><span className="font-semibold text-cyan-300">Report Date:</span> {currentDate}</p>
          <p><span className="font-semibold text-cyan-300">AI Risk Level:</span> {riskLevel || "Unknown"}</p>
          <p><span className="font-semibold text-cyan-300">Ø =</span> UI / Manual Inputs</p>
        </div>
      </div>

      <button
        onClick={() => generatePDF(reportRef)}
        className="mt-8 px-6 py-3 bg-cyan-500 hover:bg-cyan-600 text-white rounded-xl font-semibold transition-all"
      >
        Download AI Report (PDF)
      </button>
    </div>
  );
};

export default ReportPage;
