// src/pages/Reports.jsx
import React, { useRef } from "react";
import { generatePDF } from "../utils/generatePDF";

const Reports = () => {
  const reportRef = useRef();
  const currentDate = new Date().toLocaleString();

  const patientData = {
    name: "Mr. Bhavya Kansal",
    age: 22,
    riskLevel: "Low",
  };

  return (
    <div className="p-8 flex flex-col items-center justify-center min-h-screen bg-gradient-to-br from-[#0f172a] to-[#1e293b] text-white">
      {/* Report Card */}
      <div
        ref={reportRef}
        className="bg-[#1e293b] border border-[#334155] rounded-2xl shadow-lg p-10 w-full max-w-3xl"
      >
        <h1 className="text-3xl font-bold text-cyan-400 mb-6 text-center">
          AI-Powered Heart Disease Report
        </h1>

        <div className="space-y-4 text-lg">
          <p>
            <span className="font-semibold text-cyan-300">Patient Name:</span>{" "}
            {patientData.name}
          </p>
          <p>
            <span className="font-semibold text-cyan-300">Age:</span>{" "}
            {patientData.age}
          </p>
          <p>
            <span className="font-semibold text-cyan-300">Report Date:</span>{" "}
            {currentDate}
          </p>
          <p>
            <span className="font-semibold text-cyan-300">AI Risk Level:</span>{" "}
            {patientData.riskLevel}
          </p>
        </div>
      </div>

      {/* Download Button */}
      <button
        onClick={() => generatePDF(reportRef)}
        className="mt-8 px-6 py-3 bg-cyan-500 hover:bg-cyan-600 text-white rounded-xl font-semibold transition-all"
      >
        Download AI Report (PDF)
      </button>
    </div>
  );
};

export default Reports;
