import React, { useState } from "react";
import { generatePDF } from "../utils/generatePDF";

const Reports = () => {
  const [name, setName] = useState("");
  const [age, setAge] = useState("");
  const [gender, setGender] = useState("");
  const [risk, setRisk] = useState("Low"); // Default risk for testing

  const handleDownload = () => {
    generatePDF({ name, age, gender, risk });
  };

  return (
    <div className="min-h-screen bg-black text-white p-6">
      <h2 className="text-3xl font-bold text-center mb-6">ðŸ“„ Generate Your Heart Report</h2>

      <div className="max-w-xl mx-auto bg-white/10 p-6 rounded-xl space-y-4">
        <input
          type="text"
          placeholder="Name"
          value={name}
          onChange={(e) => setName(e.target.value)}
          className="w-full p-3 rounded bg-black/80 text-white border border-gray-700"
        />
        <input
          type="number"
          placeholder="Age"
          value={age}
          onChange={(e) => setAge(e.target.value)}
          className="w-full p-3 rounded bg-black/80 text-white border border-gray-700"
        />
        <select
          value={gender}
          onChange={(e) => setGender(e.target.value)}
          className="w-full p-3 rounded bg-black/80 text-white border border-gray-700"
        >
          <option value="">Select Gender</option>
          <option value="Male">Male</option>
          <option value="Female">Female</option>
        </select>

        {/* Preview Risk */}
        <div className="text-lg text-center text-yellow-400">
          <b>AI Risk Prediction:</b> {risk}
        </div>

        <button
          onClick={handleDownload}
          className="mt-4 w-full bg-blue-600 hover:bg-blue-800 text-white font-bold py-3 px-4 rounded"
        >
          ðŸ“¥ Download Report
        </button>
      </div>
    </div>
  );
};

export default Reports;
