import React, { useRef } from "react";
import jsPDF from "jspdf";
import html2canvas from "html2canvas";

const Reports = ({ data }) => {
  const reportRef = useRef();

  // If no data is passed as props, use fallback placeholders
  const patientData = data || {
    name: "John Doe",
    age: "--",
    gender: "--",
    heartRate: "--",
    spo2: "--",
    temperature: "--",
    ecg: "--",
    bp: "--",
    aiRisk: "--",
    aiSuggestion: "--",
  };

  const generatePDF = () => {
    html2canvas(reportRef.current, { scale: 3 }).then((canvas) => {
      const imgData = canvas.toDataURL("image/png");
      const pdf = new jsPDF("p", "mm", "a4");
      const pdfWidth = pdf.internal.pageSize.getWidth();
      const imgHeight = (canvas.height * pdfWidth) / canvas.width;
      pdf.addImage(imgData, "PNG", 0, 0, pdfWidth, imgHeight);
      pdf.save(`HeartReport_${patientData.name}.pdf`);
    });
  };

  return (
    <div className="min-h-screen bg-[#0f0c29] text-white p-6">
      <h1 className="text-4xl font-bold text-center mb-8 glow-text">Patient Health Report</h1>

      <div
        ref={reportRef}
        className="bg-[#1f1c2c] p-8 rounded-xl border border-purple-600 shadow-lg w-full max-w-[800px] mx-auto"
      >
        <h2 className="text-2xl font-bold mb-4 text-center text-cyan-300">Doctor's Report</h2>
        <div className="grid grid-cols-2 gap-6 text-lg">
          <div>
            <p><strong>Name:</strong> {patientData.name}</p>
            <p><strong>Age:</strong> {patientData.age}</p>
            <p><strong>Gender:</strong> {patientData.gender}</p>
            <p><strong>Heart Rate:</strong> {patientData.heartRate} bpm</p>
            <p><strong>SpO₂:</strong> {patientData.spo2} %</p>
          </div>
          <div>
            <p><strong>Temperature:</strong> {patientData.temperature} °C</p>
            <p><strong>ECG:</strong> {patientData.ecg} mV</p>
            <p><strong>Blood Pressure:</strong> {patientData.bp} mmHg</p>
            <p><strong>AI Risk:</strong> {patientData.aiRisk}</p>
            <p><strong>AI Suggestion:</strong> {patientData.aiSuggestion}</p>
          </div>
        </div>
        <p className="mt-6 italic text-center text-gray-300">* This is an AI-generated report. Please consult a licensed doctor for clinical diagnosis.</p>
      </div>

      <div className="text-center mt-6">
        <button
          onClick={generatePDF}
          className="bg-green-500 hover:bg-green-600 px-6 py-3 rounded-xl text-lg font-bold glow-button"
        >
          Download Report as PDF
        </button>
      </div>
    </div>
  );
};

export default Reports;
